  //         points={item["questions"]["points"]}
  //         />
  //     )}
  //     renderSectionHeader={({ section: { category } }) => (
  //       <Text style={styles.header}>{category}</Text>
  //     )}
  //   />
  //  })
   const Item = ({ title }) => (
    <View style={styles.item}>
      {console.log(title)}
        <Card question={title["questions"]["question"]} 
          answer={title["questions"]["answer"]}
          points={title["questions"]["points"]}
          />
    </View>
  );

    return (
      <View style={styles.container}>
        <Text style={styles.title}>Play Game!!!</Text>
        {/* {cards} */}
        <SectionList
      sections={data}
      keyExtractor={(item, index) => item + index}
      renderItem={({ item }) => (<Item title={item} />)}
      renderSectionHeader={({ section: { title } }) => (
        <Text style={styles.header}>{title}</Text>
      )}
    />

       {/* <Card question={"question"} answer={"answer"} points={"100"}/> */}

        {/* <Button
          onPress={handleStartGamePress}
          title="Start the Game"
          color="#841584"
          accessibilityLabel="Questions"
        />
        <Button
          onPress={handleBackPress}
          title="RETURN TO HOME SCREEN"
          color="#841584"
          accessibilityLabel="GO BACK!"
        /> */}


      </View>
    );
  }


  const styles = StyleSheet.create({
    container: {
      // flex: 1,
      //backgroundColor: "#ddf",
    },
    title: {
      fontSize: 60,
      alignItems: "center",
      justifyContent: "center",
    },
    button: {
      fontSize: 40,
      paddingBottom: 20,
       alignItems: "center",
      justifyContent: "center",
    },
    header: {
      fontSize: 30,
      color: "#FFF",
      marginTop: 10,
      padding: 2,
      backgroundColor: "#C2185B",
      textAlign: "center",
