    row: {
      marginHorizontal: 15,
      marginTop: 10,
      flexDirection: "row",
      justifyContent: "space-between",
      alignItems: "center",
      paddingHorizontal: 2,
    },
    rowText: {
      fontSize: 18,
    },
  });
